INSERT INTO `dvfen_jcomments` VALUES (4,0,0,'0',0,4,'com_content','','nb-NO',0,'Fredag','Fredag','fredag@oien.net','','','Nå kommer snart Tesla Model S og Renault Zoe, som ikke bruker CHADEMO-hurtigladere. Og vi har allerede en del Tesla Roadstere, som heller ikke bruker CHADEMO. Er det mulig å få et valg for biltype, og så viser kartet kun hurtigladerne som gjelder for den biltypen. Litt tidlig, men det er for å få fokus på at hurtigladere er ikke bare er CHADEMO, men kan like gjerne være en 400V 3-fas 32A kontakt, eller 240V 80A. Og at de som bygger ut størreladestasjoner nå gjerne kan installere en 240V eller 400V 3-fas 32A kontakt slik at langveisfarende Tesla-eiere også kan få ladet med en viss hastighet.','194.63.253.3','2013-01-02 13:44:10',0,0,0,0,0,'',0,0,'0000-00-00 00:00:00',''),(5,0,0,'0',0,39,'com_content','','nb-NO',0,'Geir','Geir','mcferie@hotmail.com','','','Kommer det en app for oss med windows phone også....','86.62.134.183','2013-01-04 20:06:49',0,0,0,0,0,'',0,0,'0000-00-00 00:00:00','');
